n = int(input('Digite um número para ver sua tabuada: '))
print('TABUADA DE ADIÇÃO')
print(f'{n} + 01 = {n+1} \n{n} + 02 = {n+2} \n{n} + 03 = {n+3} \n{n} + 04 = {n+4} \n{n} + 05 = {n+5} \n{n} + 06 = {n+6} \n{n} + 07 = {n+7} \n{n} + 08 = {n+8} \n{n} + 09 = {n+9} \n{n} + 10 = {n+10}')
print('TABUADA DE SUBTRAÇÃO')
print(f'{n} - 01 = {n-1:.2f} \n{n} - 02 = {n-2:.2f} \n{n} - 03 = {n-3:.2f} \n{n} - 04 = {n-4:.2f} \n{n} - 05 = {n-5:.2f} \n{n} - 06 = {n-6:.2f} \n{n} - 07 = {n-7:.2f} \n{n} - 08 = {n-8:.2f} \n{n} - 09 = {n-9:.2f} \n{n} - 10 = {n-10:.2f}')
print('TABUADA DE DIVISÃO')
print(f'{n} ÷ 01 = {n/1:.2f} \n{n} ÷ 02 = {n/2:.2f} \n{n} ÷ 03 = {n/3:.2f} \n{n} ÷ 04 = {n/4:.2f} \n{n} ÷ 05 = {n/5:.2f} \n{n} ÷ 06 = {n/6:.2f} \n{n} ÷ 07 = {n/7:.2f} \n{n} ÷ 08 = {n/8:.2f} \n{n} ÷ 09 = {n/9:.2f} \n{n} ÷ 10 = {n/10:.2f}')
print('TABUADA DE MULTIPLICAÇÃO')
print(f'{n} x 01 = {n*1} \n{n} x 02 = {n*2} \n{n} x 03 = {n*3} \n{n} x 04 = {n*4} \n{n} x 05 = {n*5} \n{n} x 06 = {n*6} \n{n} x 07 = {n*7} \n{n} x 08 = {n*8} \n{n} x 09 = {n*9} \n{n} x 10 = {n*10}')